select 'irepl -MBTV -R arcs-df.ivec.org \"'||A3.coll_name||'/'||A2.data_name||'\"' FROM ((SELECT DISTINCT data_id FROM r_data_main) EXCEPT (SELECT data_id FROM r_data_main WHERE data_repl_num > 0)) AS A1 INNER JOIN (SELECT data_id,coll_id, data_name FROM r_data_main) AS A2 ON A1.data_id = A2.data_id INNER JOIN (SELECT coll_id, coll_name FROM r_coll_main WHERE coll_name NOT LIKE '/ARCS/trash/%') AS A3 on A2.coll_id = A3.coll_id;
